INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Cofound.it', 'CFI', 'cfi.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://cofound.it/', '@cofound_it', 'Cofound.it is a decentralized platform that connects startups, experts and investors worldwide. The platform seeks to be a hub where projects can be easily kickstarted and the best projects are selected, helped with expert coaches, given promotional funds and showcased to the investor community. The CFI token powers the interactions on the platform.', '', '', '', '2019-07-03', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Status Network Token', 'SNT', 'snt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://status.im/', '@ethstatus', 'Status is an open source messaging platform and mobile browser that allows users to interact with decentralized applications (dApps) that run on the Ethereum Network. In Status, users own and control their own data, wealth and digital identity. The Status Network Token (\'SNT\') is an Ethereum-based token that is required to interact with the Status Network.', '', '', '', '2018-08-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'AventCoin', 'AVT', 'avt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://aventus.io/', '@aventussystems', 'Aventus is an Ethereum-based platform. Its economic model eliminates uncontrolled resale and counterfeit tickets. It allows event organizers to create, manage and promote their events and tickets with dramatically reduced costs, even letting them set price controls and receive commissions on ticket resales. It also gives ticket buyers rewards for promoting events, and identifying fraudulent activity.\n\nAventCoin (AVT) is the underlying token in the Aventus protocol. The purpose of AVT is to fuel the Aventus ecosystem and ensure that the protocol runs autonomously, de-centrally, and without any fraud. AVT is used for stake weighted voting and consensus mechanisms for voting on the legitimacy of:\n\n- Events on the protocol (e.g. the global pool of verified events) => fraudulent events mean applications selling their tickets will lose customers, so we need to prevent this.\n- Applications (either promoters or ticketing apps) sitting on top of the protocol => a list of verified applications needs to be determined so applications falsely claiming to use the protocol cannot sell fraudulent tickets.\n- The parameters that determine how the protocol works, e.g. event creation fees or reporting fees.\n\nIt is also used to facilitate anonymous matching of buyers and ticket sellers in the secondary market. “Matchers” are like miners in the Ethereum/Bitcoin blockchains and get rewarded with new AVT for performing these computations. The winning “matcher” for a given ticket (like the winning miner of a given block) is chosen by an AVT stake-weighted probability distribution.\n\nThey finished their ICO on the 6th of September in under 10 minutes. Raised 60k ETH in total, 30k presale, 16k white-list sale and 14k during their public token sale.', '', '', '', '2018-07-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Civic', 'CVC', 'cvc.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.civic.com/', '@civickey', 'Civic is a decentralized identity ecosystem that allows for on-demand, secure and lower cost access to identity verification via the blockchain. Through a digital Identity platform, users to set up their own virtual identity and to store it along with their personally identifiable information on the device. This information will go through a verification process conducted by the identity validators on the platform and then ported into the blockchain where service providers can access it with the proper permission from the user.\n\nCVC is an Ethereum-based token used by service providers that are looking to acquire information about a user. These can make a payment in CVC. The smart contract system employed will then see funds delivered to both the validator and the identity owner (user).', '', '', '', '2018-09-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'iXledger', 'IXT', 'ixt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://ixledger.com/', '@iXledgertech', 'InsureX is a decentralized marketplace for insurance products based on blockchain technology. This alternative marketplace aims to bring insurers, reinsurers and brokers together in an efficient, cost-effective and transparent manner that skips middlemen and unneeded fees. The InsureX (IXT) token is the underlying token used in the InsureX market by the community, customers and partners to buy data and premium services.', '', '', '', '2017-11-07', '2001-01-01 00:00:00');
