INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Starta', 'STA', 'sta.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://startaico.com/', '@startaico', 'The Starta Accelerator is an acceleration program launched by Starta Capital VC Fund in 2015. It seeks to empower Eastern European frontier technology companies by exposing them to the U.S. market and providing training in various areas. Starta identifies and helps these companies while also getting a share of the company, then exiting at with a profit. The Starta token is a Waves-based asset that represents a share in the portfolio of the \"Starta Accelerator 16/17\", allowing holders to profit from this business model. The portfolio at hand includes multiple startups, mostly tech-oriented.', '', '', '', '2017-04-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Ergo', 'EFYT', 'efyt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://ergoplatform.org/', '@ergoplatformorg', 'Ergo will be a brand new and unique, decentralized Proof-of-Work blockchain platform with state-of-art efficiency and a new transactional language. Ergo aims to fix the most pressing issues in the cryptosphere such as scalability and centralization, while offering multiple improvements. Ergo will use a proof system known as PoPoW or “Proofs of Proof of Work”.\n\nThe EFYT(Ergo First Year Tokens) token is an asset issued on the Waves platform and it is backed by the Ergo tokens which will be destributed to holders of the EFYT.', '', '', '', '2017-02-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Tezos', 'XTZ', 'xtz.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'DPoS', 'https://www.tezos.com/', '@tez0s', 'Tezos is a new decentralized blockchain that governs itself by establishing a true digital commonwealth. It facilitates formal verification, a technique which mathematically proves the correctness of the code governing transactions and boosts the security of the most sensitive or financially weighted smart contracts.\n\nTezos takes a fundamentally different approach to governance by creating governance rules for stakeholders to approve of protocol upgrades that are then automatically deployed on the network. When a developer proposes a protocol upgrade, they can attach an invoice to be paid out to their address upon approval and inclusion of their upgrade. This approach provides a strong incentive for participation in the Tezos core development and further decentralizes the maintenance of the network. It compensates developers with tokens that have immediate value rather than forcing them to seek corporate sponsorships, foundation salaries, or work for Internet fame alone.', '', '', '', '2017-01-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Monaco', 'MCO', 'mco.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://mona.co/', '@monaco_card', 'The functionality of Monaco VISA® Card makes it a must-have item not only for every Ether or Bitcoin holder, but also for everyone who spends money in foreign currency.\n\nCore features:\n\n\\- Spend your Ether or Bitcoin with a physical Monaco VISA® Card, wherever VISA® cards are accepted, both online & offline\n\n\\- Only the exact amount that you spend with your Monaco VISA® Card will be incrementally exchanged from ETH/BTC in real-time when you swipe your card, with rest being securely stored in your wallet to which only you have access\n\n\\- Exchange money at perfect interbank exchange rates using Monaco App\n\n\\- Enjoy the same perfect interbank rates whenever you spend on Monaco VISA® Card\n\n\\- Send money from Monaco App in 23 currencies to 120 countries worldwide for free\n\n\\- Sending money between Monaco App users is free & real-time, with the money being instantly available for the recipient on their Monaco VISA® Card - Customer onboarding can be done entirely via the mobile App – a process taking less than 3 minutes', '', '', '', '2018-06-05', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Numerai', 'NMR', 'nmr.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://numer.ai/', '@numerai', ' Numerai is a network of data scientists that are incentivised to forecast models to improve hedge fund returns. Numaire is the token that facilitates the reward structure that correct predictions receive. Founders described it as a social network or software company whose business model happens to be a hedge fund. USV partner Andy Weissman says, “Network effects get more valuable with every participant that uses them. What’s an example of that in a financial institution? We’ve never seen one before except for this.”\n\nNumerai has already raised $7.5 million in traditional venture capital from Union Square Ventures, Joey Krug (Augur), Juan Benet (FileCoin), Fred Ehrsam (Coinbase), and Olaf Carlson-Wee (Polychain). So the Numeraire token will not be sold in a “crowdsale” or “ICO”.\n\nIn Febuary 2017 1,000,000 NMR tokens were issued to 12,000 data scientists.\n\n[White paper](https://numer.ai/whitepaper.pdf)\n\n[Forum](https://forum.numer.ai/)', '', '', '', '2018-09-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'AdEx', 'ADX', 'adx.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.adex.network/', '@AdEx_Network', 'AdEx is a decentralized Ad exchange built on the Ethereum network Focusing on the entertainment industry and video streaming services and publishers, AdEx aims to replace the traditional digital advertising models by providing a transparent, focused solution for advertisers to collaborate with ad publishers directly, skipping middlemen fees and complications. The platform also ensures that the end user can understand and control the ads delivered to them through the use of the AdEx User Profile personalized page. The AdEx token (ADX) is used within the AdEx Platform to buy or sell advertising space and time.', '', '', '', '2019-06-05', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Quantum', 'QAU', 'qau.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://www.quantumproject.org/', '@QAUProject', 'The Quantum project aims to bring institutional grade liquidity to the cryptocurrency and digital asset markets, which is currently one of the main impediments to large scale adoption. Quantum will do this by deploying liquidity pools which will provide funds  for margin trading to exchanges, connecting various markets by arbitraging price differences and making markets using price neutral algorithmic trading. Any income generated from the liquidity pool will be used to buy back Quantum tokens on the market at the best possible price and destroy them publically, making the QAU token a deflationary currency.', '', '', '', '2018-03-04', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'EcoBit', 'ECOB', 'ecob.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.ecobit.io/', '@EcoBit_io', 'EcoBit is a NEM-based token. The fund gathered during the ICO are to be used to participate in green-related projects. The returns from the projects are reinvested in developing more green projects, which benefit the communities and token holders, plus provides an income stream to token holders.', '', '', '', '2017-09-04', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Polybius', 'PLBT', 'plbt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://polybius.io/', '@PolybiusBank', 'Polybius is a fully-digital bank designed to acomodate the growing digital economy, incorporating electronic identification and digital banking services to serve the needs of the digital commerce market. The Polybius is an enterprise of HashCoins, the same company that brings you the Hashflare mining contracts.\n\nIn order to fund the development of this new digital bank, Polybuis hosted an Initial Coin Offering, in which PLBT tokens were distributed among users. Through the use of Blockchain Snapshots, Polybius will destribute dividends to the PLBT token holders.', '', '', '', '2019-06-03', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Tether', 'USDT', 'usdt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://tether.to/', '@Tether_to', 'USDT is a cryptocurrency asset issued on the Bitcoin blockchain via the Omni Layer Protocol. Each USDT unit is backed by a U.S Dollar held in the reserves of the Tether Limited and can be redeemed through the Tether Platform. USDT can be transferred, stored, spent, just like bitcoins or any other cryptocurrency.\n\nUSDT and other Tether currencies were created to facilitate the transfer of national currencies, to provide users with a stable alternative to Bitcoin and to provide an alternative for exchange and wallet audits which are currently unreliable. USDT provides an alternative to Proof of Solvency methods by introducing a Proof of Reserves Process.\n\nIn the Tether Proof of Reserves system, the amount of USDT in circulations can be easily checked on the Bitcoin blockchain via the tools provided at Omnichest.info, while the corresponding total amount of USD held reserves is proved by publishing the bank balance and undergoing periodic audits by professionals.', '', '', '', '2014-10-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'RaiBlocks', 'XRB', 'xrb.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'https://raiblocks.net/', '@raiblocks', 'RaiBlocks is designed to be a **low latency**, **high throughput** cryptocurrency. It builds on an analogy from the electrical engineering discipline by equating network consensus to arbiters circuits. This gives RaiBlocks an established and well researched modeling basis for how the system comes to a distributed, egalitarian, and efficient conclusion. In the RaiBlocks system, each account in the system has a block chain that is controlled only by them, all chains are replicated to all peers in the network, removing block intervals, mining, transaction fees.', '', '', 'Blake2b', '2018-05-02', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Ahoolee', 'AHT', 'aht.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'http://ahoolee.io/', '@Ahooleeman', 'Aholee is a decentralized search engine for products, dedicated to online shopping worldwide. Aholee uses its own in-house blockchain, which is an Ethereum fork, for open collecting and indexing information from open sources with confirmation of authenticity based on blockchain technology. The AHC token is used to purchase items on the Aholee platform.  \n[](https://ahoolee.com/)\n\n\n\n\n\nAny AHC owner can purchase products using AHC in any store represented in the platform at the current AHC exchange rate to the currency used by the store. Any visitor to ahoolee.com can buy AHC, as this will give them an opportunity to get additional discounts from stores.', '', '', 'Ethash', '2017-08-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ATB coin', 'ATB', 'atb.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'https://atbcoin.com/', '@atbcoincom', 'ATB coin is a Proof of Stake cryptocurrency designed to solve some of the most pressing issues in the current crypto landscape, such as scalability, energency efficiency and more. ATB coin features some of the most recent technologies in the blockchain space such as the SegWit update and Lightning Network making it more efficient, flexible, and agile. ATB coin comes with an array of lightweight and user-friendly wallets—for desktop, iOS, Android, and web.', '', '', 'SHA256', '2017-12-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Blocktix', 'TIX', 'tix.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://blocktix.io/', '@blocktix', 'Blocktix is a decentralized platform built for ticket sales and event promotion, a decentralized ticket distribution network facilitating the process for event organizers and fans alike. The system is built on Ethereum smart contracts, allowing users to verify the validity tickets for a given event, putting an end to the growing counterfeit grey economy.\n\nBlocktix provides a user-friendly interface for the execution of smart contracts, bringing its advanced capabilities to the masses with an intuitive application. This approach reduces the complexity of the interaction with smart contracts and provides an additional layer of security minimizing the possibility of fraud or malicious third party interference with the application due to the standardization of contracts.\n\nTIX is an Ethereum-based token that grants several benefits to its holders within the Blocktix system. By participating in the event verification process, the token holder will receive a reward. The initial reward will be the TIX that is used to back the event. Users will also be able to purchase advertising rights for an event. These funds will be distributed to all tokens holders who took part in the event verification process, proportional to the amount of tokens they own.', '', '', '', '2017-07-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ChanCoin', '4CHN', '4chn.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', NULL, NULL, 'ChanCoin is a Proof of Work cryptocurrency based on the Scrypt algorithm. The development team has plans to implement a browser extension that allows users of 4chan boards to tip each others posts.', '', '', 'Scrypt', '2019-04-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Compcoin', 'CMP', 'cmp.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://compcoin.com/', NULL, 'Compcoin is an investment system that uses a valuable blockchain Digital Asset to run a decentralized financial trading computer. Compcoin provides the capacity for automatically generated cash flows using Artificial Intelligence, the ART – a proprietary, automated, algorithmic foreign currency exchange (forex) trading platform developed by Fintech Investment Group.\n\n ART analyzes historical, back-tested forex trading data to predict the potential outcomes of future US Dollar-to-Euro (USD/EUR) currency trades, and automatically executes those trades on behalf of investors.\n\nCompcoin can be transferred directly between users without the need of an intermediary. Once Compcoin becomes available, open, convertible coin (estimated mid-2017), Compcoin will be available for trade with Bitcoin, Ether or other digital coins on one or more electronic exchanges.', '', '', '', '2018-01-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Rivetz', 'RVT', 'rvt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://rivetzintl.com/', '@rivetzcorp', 'Rivetz works through the Trusted Execution Environment: a private hardware “vault” on your phone that is completely independent from the operating system. Inside of this vault, everything is isolated – so no apps or software or operating system can see what’s inside. This is where Rivetz works to securely manage your secrets, your passwords, keys, and other private information.\n\nRvT Tokens power Rivetz: RvT is an Ethereum-based token that works with the blockchain to cryptographically prove that the vault in your phone is secure, all the way from the manufacturer to your pocket. The RvT blockchain can perform a health check, verifying that your vault’s integrity is intact, and that transactions you send are exactly the ones you intended.', '', '', '', '2019-01-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Nimiq Exchange Token', 'NET*', 'net_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://nimiq.com/', '@nimiqnetwork', 'Nimiq is a Browser-based Blockchain & Ecosystem Written in Javascript ES6 designed to make cryptocurrency easier for the enduser. Using WebRTC and WebSocket connections, Nimiq\'s ecosystem native to the web, allowing users to sync in seconds and to mine directly from the browser.\n\nThe Nimiq project is not live yet and the team has issued IOU tokens that can be traded. These tokens are callred Nimiq Exchange Tokens (NET) and they are ERC20 tokens based on the Ethereum blockchain. NET tokens will later be exchangeable for real Nimiq tokens.', '', '', '', '2017-06-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, '8 Circuit Studios', '8BT', '8bt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://8circuitstudios.com/', '@8circuitstudios', 'Circuit Studios is creating a new gaming ecosystem powered by their 8BT tokens, an Ethereum-based token. These tokens allow gamers to purchase game assets. The first release of Circuit Studios is D-PARC, described as \"a new game that uses the Ethereum blockchain to enable digital sovereignty\".', '', '', '', '2018-05-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'CoinDash', 'CDT', 'cdt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.coindash.io/', '@coindashio', 'Coindash​ is an operating system for blockchain-based assets, it has the goal of making handling and trading easy and accessible for both new and experienced users in the cryptoshpere. The CoinDash’s ecosystem includes three synergistic components: Software-as-a-Service (Saas), Social Networking and the Marketplace.\n\n**SaaS:** CoinDash provides analytics that give users deep insight into the performance of their investments and help them make data driven decisions.\n\n**Social Network:** CoinDash provides social trading features that remove barriers for new and existing crypto users by letting them follow and copy-trade top investors.\n\n**Marketplace:** CoinDash’s marketplace is built around opportunity discovery and streamlined trading UX with features like real-time social trading signals, an ICO dashboard, trending assets, real-time market price tables and 3rd party integration. ​\n\nEvery action within the CoinDash platform requires the user to pay a fee which is paid in the CoinDash (CDT), an Ethereum-based token Every user who facilitates the use of a feature which in turn gives financial value will be entitled to a payment, creating a creator-consumer relationship between users in the system.', '', '', '', '2019-04-03', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ACT', 'ACT', 'act.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.daoact.org/', '@daoact_org', 'ACT is a decentralized autonomous organization (DAO) created to address social accountability by using aggregated capital that is contributed by citizens in order to fund grass roots proposals that drive change on pressing social, political or economical issues.', '', '', '', '2019-01-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'district0x', 'DNT', 'dnt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://district0x.io/', '@district0x', 'District0x Network is a collective of decentralized marketplaces and communities, hereafter referred to as districts.\n\nDistricts exist as decentralized autonomous organizations on the district0x Network, built upon a standard open source framework comprised of Ethereum smart contracts and front-end libraries, hereafter referred to as d0xINFRA. d0xINFRA provides districts with the core functionalities required to operate an online market or community.\n\nThe district0x Network Token can be used to signal what districts should be built and deployed by the district0x Project team and can be staked to gain access to voting rights in any district on the district0x Network.', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Suretly', 'SUR', 'sur.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://suretly.com/', '@suretly_com', 'Suretly is a provider of an international crowd vouching platform. The company offers users an investment alternative called _crowdvouching_, which allows customers to make money vouching for people who need an urgent loan.\n\nThis system works in a similar fashion with peer-2-peer lending, but customers do not lend money to each other directly. Instead, they vouch for borrowers who apply for a loan through lending partners.\n\nCustomers can find borrowers that are looking for their loan’s guarantors. The voucher can check the borrower’s profile and choose whether to vouch for the borrower if their default risk and earning amount suit them.', '', '', '', '2018-08-04', '2001-01-01 00:00:00');
