INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'DAO.casino', 'BET*', 'bet_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://dao.casino/', '@daocasino', 'DAO.Casino is a decentralized casino protocol, allowing eveloper to use Dao.Casino protocol, rather than work on its own, and less costly for a casino operator to provide better service for the players. This also allows gamblers to have a wider variety of games, rather than having the ones provided by one single casino. The aim of the DAO.Casino protocol is to eliminate single points of failure in the online gambling industry through the use of Ethereum smart contracts.\n\nBET is an ERC20 token on the Ethereum blockchain and it is used both as an ingame currency and as a reward token that can be distributed to all participants equally. All the participants contributing to the ecosystem in one form or another are automatically rewarded with BET to their accounts.', '', '', '', '2018-10-05', '2001-01-01 00:00:00');
