INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Wagerr', 'WGR', 'wgr.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.wagerr.com/', '@wagerrx', 'Wagerr is a decentralized sportsbook built on blockchain technology. Due to its peer-to-peer structure Wagerr brings trustless sports betting to the whole world while avoiding single hacks, scams and server downtime. Wagerr is able to escrow user stakes, verify results and pay out winners automatically, eliminating the need for third party services which charge higher fees.\n\nIn Wagerr ets are executed through the use of Application Specific Smart Contracts (ASSC), while a network of Oracle Masternodes update game results and execute smart contracts to facilitate reliable betting and payouts and receive 50% of the bet\'s fee while doing so. 48% of the fees are destroyed, reducing the total supply of WGR while demand is created by users who buy WGR to gamble with. The current Wagerr token is a transitionary token issued on the Waves Platform. It\'s a provisionary token that can be exchanged freely on the Waves decentralized exchange before the Wagerr mainnet launches.', '', '', 'PoS', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Elastic', 'XEL', 'xel.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'https://www.elastic.pw/', '@elastic_coin', 'Elastic is an open-source project that provides the infastructure for a decentralized supercomputer. r, designed to carry out arbitrary tasks over the internet. Those who need computational resources, model their problem using Elastic’s programming language (Elastic PL) and broadcast it on the network, along with a certain amount of XEL coins. The Elastic miners are then motivated to offer their computational resources in exchange for a portion of those XEL coins.\n\nElastic offers potential buyers a large parallel computation cluster composed of many CPUs and GPUs supplied by the miners. The network is powered by its own PoW cryptocurrency (XEL) and provides a market-based mechanism to buy and sell computational resources.', '', '', '', '2017-08-06', '2001-01-01 00:00:00');
