INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Pillar', 'PLR', 'plr.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://pillarproject.io/', '@PillarWallet', 'The Pillar wallet will hold keys and let anyone transact with many blockchains (multi-chain wallet), will be able to see hundreds of kinds of tokens and coins. The Pillar wallet will not be tied with any browser, nor to an exchange (option to choose multiple exchanges).\n\nWhile the wallet and platform are open-source and free, users will pay for the various services with pillar tokens (PLRs) as they use them. The token will power the entire ecosystem, giving an instant business model to many projects that can tie in.', '', '', '', '2019-02-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'GeoFunders', 'GUNS', 'guns.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://www.geofounders.com/', '@geofounders', 'GeoFounders is a software collective focused on building blockchain-related applications and services. Using tokens called GeoUnits (GUNS), revenue collected by the GeoFounders software catalogue is automatically divvied up for token holders via smart contracts and trusted oracles. A small portion of the revenue is cycled back into the collective to further increase the collective\'s application catalogue, which in turn increases the token value and revenue payouts.\n\nGeoFounders tokens, also known as GeoUnits (GUNS), serve multiple purposes. The first purpose is to act as a measurement of “virtual stake” regarding revenue produced by the GeoFounders software catalogue. GUNS tokens are analyzed using a simple formula due to the fixed nature of the GUNS supply. For every GUNS token a person possesses, they receive 0.0000009% of the revenue generated by the GeoFounders software collective. The more tokens you possess, the greater your share of the revenue.  \nThe second purpose of GUNS tokens are to act as a verification mechanism within different GeFounder applications. Depending on the software, this authentication process can provide different services. For example, the primary GeoFounder members application uses token verification to allow communication within the software while other applications provide novelty software rights, such as the ability to name an in-game location within GeoRealms if you can verify an account with 1000 or more GUNS.', '', '', '', '2018-08-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'InvestFeed', 'IFT', 'ift.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.investfeed.com/', '@investfeed', 'InvestFeed is currently a cross-platform social trading platform in production for US Equities, available on Browser, Native iOS, and Native Android.\n\nIn InvestFeed’s system, FEED Tokens (IFT) will play a key role in providing economic incentives so that the rational behavior of individuals results in common good. On InvestFeed’s platform, contributors will be rewarded for their work and incentivized to continue increasing the value of the community ecosystem.\n\nIFT Tokens will be of a standardized ERC20 form, so they can easily be integrated. Every feature in the InvestFeed platform that gives any added value to its user will require payment using IFT Tokens. Every user who facilitates the use of a feature which in turn gives added value, will be entitled to receive IFT Token (IFT).', '', '', '', '2019-04-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'BitClave', 'CAT*', 'cat_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://bit.ly/', '@bitclave', 'BitClave is focusing on a distributed blockchain-based system, called the BitClave Active Search Ecosystem (BASE), which allows the storing and managing of vast amounts of data consisting of records of customer activities from an unlimited and variegated number of attestation points.\n\nOn top of this distributed activity data collection system, BitClave is introducing a token, called the BitClave Consumer Activity Token (CAT), to be used internally within the blockchain among the participating parties. CAT tokens will be used to facilitate rewards within the system for all sorts of services that available on the BASE. Customers and retailers can interact directly through transparent and consumer-authorized message posts and data bundles on the public blockchain, and additional parties can contribute as service providers by creating analytics capabilities that operate over the data in the blockchain.', '', '', '', '2017-05-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Propy', 'PRO', 'pro.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://www.propy.com/', '@PropyInc', 'Propy Utility Token (PRO) is built on the ERC20 token standard to allow for both simple integration into users’ wallets.\n\nPropy has a scalable business model. When a property is purchased, Propy takes a small percentage (starting from 0.2% and averaging 1%) of the final purchase price. Propy charges brokers per transaction.\n\nTokens paid for writing data to the Propy Registry are stored in a special Rewards smart contract on the Ethereum blockchain. These aggregated amounts of tokens are paid out to the token holders, proportional to the PRO owned. The rewards contract generates a token distribution snapshot every month and distributes rewards for the quarter according to the snapshot. The snapshot is stored in the blockchain, so anyone can verify that rewards were distributed correctly.', '', '', '', '2019-01-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'SynchroCoin', 'SYC', 'syc.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://synchrolife.org/', '@synchrocoin', 'SynchroLife’s original token, the SynchroCoin (SYC), will be on the Ethereum blockchain and used to reward users who post high quality restaurant reviews and photos, add new restaurants, update business information, edit information, add information, or translate the restaurant information, and more.\n\nUsers who contribute to the improvement and growth of the platform will be rewarded for their time and content. The SynchroCoin (SYC) token will also be combined with Ethereum smart contracts to allow users to get a small percentage of advertising and marketing fees which restaurants they have provided content or information for pay.', '', '', '', '2017-06-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Indorse', 'IND', 'ind.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://indorse.io/', '@joinindorse', 'Indorse is a platform using new models of tokenization and decentralization to change the shape of professional social networking.\n\nIndorse uses internal rewards (Indorse Rewards) and a reputation system (Indorse Score) to incentivize members to add their skills / accomplishments and indorse those of others.\n\nThe core features of the Indorse platform will be implemented through the combination of a few technologies, which include Ethereum, IPFS/Swarm, and Whisper. The Indorse platform will have a serverless, decentralized architecture, with the content and code on IPFS, and the Ethereum blockchain as the computational engine.', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bowhead Health', 'AHT*', 'aht_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://bowheadhealth.com/', '@Bowheadhealth', 'Bowhead is a company with a team of medical doctors, biochemistry and data scientists, software and robotics engineers, designers and wellness coaches.\n\nBy using Blockchain, health records can be inter-operable across different platforms, hospital systems and services, while remaining secure and up to date.\n\nBowhead believes that health data is some of the most valuable data in the world. Bowhead will use anonymized customer health data to add value, while ensuring data is protected using cryptography. Customers gain access to healthcare and may also benefit financially by leasing access to their data in an anonymized manner.', '', '', '', '2019-06-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'TribeToken', 'TRIBE', 'tribe.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://tribetoken.org/', '@tribetoken', 'TribeToken is a decentralized platfrom design to bring ease and transparency to the charity industry. It uses cryptocurrency tokens to enable instant and verifiable donations. Using TribeToken users can create their own charity and donate to other charities. TRIBE is an ERC20 token on the Ethereum blockchain and is used for transactions within the TribeToken Platform.', '', '', '', '2017-06-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, '0x', 'ZRX', 'zrx.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.0xproject.com/', '@0xproject', '0x is an open protocol that facilitates the decentralized exchange of Ethereum-based tokens and assets. Developers can use 0x to build their own custom exchange apps with a wide variety of user-facing applications i.e. 0x OTC, a decentralized application that facilitates trustless over-the-counter trading of Ethereum-based tokens.\n\nThe 0x token (ZRX) is used by Makers and Takers to pay transaction fees to Relayers (entities that host and maintain public order books). ZRX tokens are also used for decentralized governance over 0x protocol’s update mechanism which allows its underlying smart contracts to be replaced and improved over time.', '', '', '', '2018-03-08', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Tierion', 'TNT', 'tnt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://tierion.com/', '@tierion', 'Tierion is a platform for data verification. Tierion works by creating a proof that links data to a transaction on a blockchain. This is called anchoring. Anyone with this proof can verify the data’s integrity and timestamp without relying on a trusted authority.', '', '', '', '2018-05-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Presearch', 'PRE*', 'pre_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.presearch.io/', '@decentralsearch', 'Presearch is an open, decentralized search engine that rewards community members with Presearch tokens for their usage, contribution to, and promotion of the platform.', '', '', '', '2017-05-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'COSS', 'COSS', 'coss.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://coss.io/', '@cosscrypto', 'COSS stands for Crypto-One-Stop-Solution and represents a platform, which encompasses all features of a digital economical system based on cryptocurrency. The COSS system consists of a payment gateway / POS, an exchange, a merchant list, market cap rankings, a marketplace, an e-wallet, various coin facilities and a mobile platform. The COSS platform unifies all transactional aspects that are usually managed by means of FIAT money, and offers multiple cryptocurrency-related services in one place.', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Storm', 'STORM', 'storm.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://stormtoken.com/', '@Storm_Token', 'STORM is an ERC20 compliant token operating on the Ethereum blockchain.\n\nStorm will use the blockchain to expand their platform to create StorMarket, a decentralized micro-task marketplace economy. StorMarket will be a place where people can go to utilize their skills and talents to complete various tasks. In addition, automation via blockchain technology will allow for all of the project management and marketing aspect of a micro-task to be integrated into the platform.', '', '', '', '2017-06-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Storj', 'STORJ', 'storj.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://storj.io/', '@storjproject', 'Storj is a decentralized cloud storage platform that allows anyone to rent their idle hard drive space and to earn a revenue by doing so. Endusers can use Storj to store their files at competitive prices and within a p2p network that is secure from sever downtime, censorship and hacks. Payments within the Sotrj network and conducted with the STORJ token, an ERC20 Ethereum-based token.', '', '', '', '2018-07-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Scorecoin', 'SCORE', 'score.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'http://scorecoin.site/', '@ScorecoinMark', 'Scorecoin is a Proof of Work cryptocurrency based on the Scrypt algorithm.', '', '', 'Scrypt', '2018-11-02', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'OmiseGo', 'OMG', 'omg.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoS', 'https://omg.omise.co/', '@omise_go', 'OmiseGO is building a decentralized exchange, liquidity provider mechanism, clearinghouse messaging network, and asset-backed blockchain gateway. OmiseGO is not owned by any single one party. Instead, it is an open distributed network of validators which enforce behavior of all participants. It uses the mechanism of a protocol token to create a proof-of-stake blockchain to enable enforcement of market activity amongst participants.\n\nOwning OMG tokens buys the right to validate this blockchain, within its consensus rules. Transaction fees on the network including payment, interchange, trading, and clearinghouse use, are given to non-faulty validators who enforce bonded contract states. The token will have value derived from the fees derived from this network, with the obligation/cost of providing validation to its users.', '', '', '', '2018-11-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Octanox', 'OTX', 'otx.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW/PoS', 'https://octanox.org/', '@OctanoxPlatform', 'Octanox is decentralized open source cryptocurrency with x11 algorithm and using POW and POS hybrid systems. They founded in April 2017 and aim to release a suite of 6 products over the following 6 months. MiddleX, OctaGUI, OTXPay, SmartWeb Wallet, OctaneX and Octalk. The miners will be able to earn about 0.8 Octanox per transaction fees and blocked mined. And in every ten million blocks, that reward will reduce until Octanox gets a point where the fess of the transaction is enough to sustain its miners. Meanwhile, the reward of POS system or proof of stake mining will be given to those who have a large of Octanox. The more Octanox you have, your earnings will be bigger through POS system.', '', '', 'X11', '2019-01-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Equibit', 'EQB', 'eqb.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://equibitgroup.com/', '@equibit', 'Equibit is a peer-to-peer network for creating and transferring equity. This allows issuers and investors anywhere to instantly transfer title to equity units called ‘Equibits’ without any reliance on a transfer agent, and at practically no cost.\n\n With the use of Equibits issuers can restrict their units to prevent transfers of their equity to unauthorized parties.', '', '', '', '2017-05-11', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Voise', 'VOISE', 'voise.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.voise.com/', '@voisecom', 'The Voise is an Ethereum based music streaming platform. VOICE tokens are the framework for the Voice´s environment, serving as the currency that rewards content creators and is charged to the streamers.', '', '', '', '2019-04-03', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'EthBits', 'ETBS', 'etbs.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.ethbits.com/', '@ethbits', 'The ETHBIT token is a type of currency used to pay fees on the ETHBITS Local platform (for example buy/sell Bitcoin, Ether and other cryptocurrencies). ETHBIT tokens can also be used to distribute dividends to investors. If you hold ETHBITS tokens, you hold a portion of our platform, and by holding a portion of our platform you are entitled to your share of a distribution of 40% of the platform\'s profit. The dividends will be paid on a quarterly basis.', '', '', '', '2018-03-04', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Crypviser', 'CVCOIN', 'cvcoin.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://ico.crypviser.net/', '@cvcoin_ico', 'Crypviser uses blockchain technology to develop solutions for B2C and B2B markets. By providing genuine encryption key identification, Crypviser can prevent manipulation, interceptions MITM attacks on all communication levels.\n\nCrypviser has developed a security model, which is designed to meet the highest standards of cryptography for securely exchanging and storing all kinds of data.', '', '', '', '2018-06-05', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'DCORP', 'DRP', 'drp.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.dcorp.it/', '@DcorpVC', 'DCORP is autonomous, decentralized and democratic. An organization that exists on blockchain as a series of smart-contracts. DCORP manages its own token (DRP) and the Ether it holds.\n\nThe shareholders (owners of DRP token) are the collective owners of DCORP and decide on the business financials. The Board of Directors manage DCORP on a day-to-day basis, are elected and publicly known. All voting behavior is stored on the blockchain and thus made public.', '', '', '', '2018-10-05', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ArcticCoin', 'ARC', 'arc.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'https://arcticcoin.org/', NULL, 'ArcticCoin is a PoW cryptocurrency based on the X11 algorithm. It has a 60M supply.', '', '', 'X11', '2015-12-07', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bogcoin', 'BOG', 'bog.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'http://bogcoin.org/', NULL, 'Bogcoin is a Proof of Work cryptocurrency based on the SHA256 algorithm (the same as Bitcoin). It\'s a meme-based cryptocurrency.', '', '', 'SHA256', '2019-01-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'NeverDie', 'NDC', 'ndc.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://neverdie.com/', '@nevereverdie', 'The purpose of the NeverDieCoin and is to turn the mechanics of buying a new life in a game, or traveling within a game or between games into a utility that requires universal tokens. With a limit to the number of tokens in circulation, these utility tokens gain an intrinsic value as the demand to utilize them grows. The tokens will be consumed each time they are used within a game and divided through smart contract design and API into fragments to be re-looted, mined, or collected and re-crafted with player skill so that they can be traded between players and used again. This will create a turnkey starting point for all game economies, as players will need to harvest and trade between each other in raw virtual materials to pay for their new lives or to hop between games.\n\nAs each token is consumed through the utility, a fragment of the token will also be assigned to the game developer as operating income and into other funds. A percentage of each token consumed will be burned.', '', '', '', '2019-06-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Po.et', 'POE', 'poe.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://po.et/', '@_poetproject', 'Po.et is an Ethereum-based decentralized ledger built to track ownership and attribution for digital creative assets. It allows users to generate immutable and timestamped titles for creative works and register their assets to the Po.et network. Po.et also gives both publishers and content creators the tools to automate the licensing process without relying on any third parties, making the process cheaper and smple.\n\nPOE is an ERC20 Ethereum-based token that represents a proportional share of the the fees and revenue generated from the commercial applications built on top of the Po.et protocol, including generating content licenses and processing license payments among others.', '', '', '', '2017-08-08', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'AdToken', 'ADT', 'adt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://adtoken.com/', '@ad_chain', 'AdChain is an Ethereum-based digital advertisement platform that aims to fix  the fraudulent environment of online advertising namely bot traffic, malvertisements, trackers, spoofed domains, lack of coordination and systemic fraud. ADT is an ERC20 token that is used in order to ver publishers who wish to join the system, employing a \"challenge period\" during which any ADT holder who believes the publisher is fraudulent can issue a challenge and match the publisher\'s ADT deposit.', '', '', '', '2019-02-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Aeternity', 'AE', 'ae.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW/PoS', 'https://www.aeternity.com/', '@aetrnty', 'Aeternity is a scalable blockchain platform that enables high bandwidth transacting, purely-functional smart contracts, and decentralized oracles.\n\nThe use of the blockchain is not free, and requires that the user spends a token called Aeon. Aeon\'s are used as payment for any resources one consumes on the platform, as well as the basis for financial applications implemented on the platform. All system fees get paid with aeon, all smart contracts settle in aeon.', '', '', '', '2018-05-12', '2001-01-01 00:00:00');
