INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bitcore', 'BTX', 'btx.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'http://www.bitcore.cc/', '@bitcore_btx', 'Bitcore is a Bitcoin-based cryptocurrency created through a snapshot and airdrop process. Bitcore has 10 MB blocks with Segwit enabled which allows for the network to handle about 17.6 billion transactions per year. It also features an ASIC resistant pow algorithm, Timetravel10, and a difficult adjustment called 64\\_15 that ensures that there is no more than a 15 percent change in difficulty within a 64 block period.', '', '', 'Time Travel', '2018-11-04', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'iDice', 'ICE', 'ice.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://idice.io/', '@idice_io', 'iDice is a dice gambling Dapp powered by the Ethereum network. iDice allows players to, place bets in units of Ether. Bet on the house by holding iDice tokens. 100% of all profit iDice earns is distributed amongst token holders, proportional to the number of tokens they hold. iDice token holders enjoy the advantage of the house edge always being in their favor. Token holders should always return a profit according to the law of large numbers. The house edge will be set competitively and token holders have an expected value that is always equivalent to the house edge. iDice has a fully transparent source code available at etherscan.io. iDice does not require deposits nor sign ups. The payout of games is always immediate. It is the first blockchain dice game to incorporate mobile applications.', '', '', '', '2017-12-06', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Sphre AIR', 'XID', 'xid.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://www.sphre.co/', '@sphreco', 'Air claims to be a highly secure platform for digital identity, based on the Hyperledger blockchain. The baseline technology of Air is composed of three major components: Chaincode (also known as a Smart Contract), Application Programming Interface (API) and a mobile/cellphone application (initially for Apple iOS/Google Android, with other platforms to follow) for individual use.', '', '', '', '2017-04-07', '2001-01-01 00:00:00');
