INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bitcoin Cash / BCC', 'BCH', 'bch.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'https://www.bitcoincash.org/', NULL, 'Bitcoin Cash (BCH) is a hard forked version of the original Bitcoin. It is similar to bitcoin with regards to its protocol; Proof of Work SHA-256 hashing, 21,000,000 supply, same block times and reward system. However two main differences are the the blocksize limits, as of August 2017 Bitcoin has a 1MB blocksize limit whereas BCH proposes 8MB blocks. Also BCH will adjust the difficulty every 6 blocks as opposed to 2016 blocks as with Bitcoin.\n\nBitcoin Cash is a proposal from the viaBTC mining pool and the Bitmain mining group to carry out a UAHF (User Activated Hard Fork) on August 1st 12:20 pm UTC. They rejected the agreed consensus (aka BIP-91 or SegWit2x) and have decided to fork the original Bitcoin blockchain and create this new version called “Bitcoin Cash”. Bitcoin Cash can be claimed by BTC owners who have their private keys or store their Bitcoins on a service that will split BCH for the customer.\n\n[Specification](https://github.com/Bitcoin-UAHF/spec/blob/master/uahf-technical-spec.md)', '', '', 'SHA256', '2017-01-08', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'DigiPulse', 'DGPT', 'dgpt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.digipulse.io/', '@DigiPulseIO', 'DigiPulse is a SaaS platform that charges a subscription fee for memberships. The subscription fee is paid via fiat currencies or in DGP - the DigiPulse coin. It is a platform where the user can store information (letters, photos, miscellaneous information), write personal notes and have integrations from various other services (e.g. Coinbase).\n\nThe platform automatically establishes the status of the user (the period of inactivity) via a desktop app, a smartphone app and/or other web based integrations, and the user is the one who determines which integrations he connects to his account and the way his activity should be tracked, and the inactivity period that triggers the data send out.', '', '', '', '2017-04-08', '2001-01-01 00:00:00');
