INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Credo', 'CFT*', 'cft_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://credo-cf.com/', '@https://twitter.com/CREDO_platform', 'Credo is a payment system based on a financial algorithm that involves the participation of an independent agent in the transactions of purchase and sale of goods or services. The system acts as an agent between the buyer of goods and the seller, providing the parties with payment processing for a profitable transaction. The token allows users to participate in the network and benefit from the Credo services. The presale raised just over 100 ETH, and was setup to collect development funds, collect feedback and improve the marketing design for the main launch.\n\nThe financial system is made on the algorithm of issue of electronic derivative financial instruments (certificates), which are a means of payment and represent the property right to claim of the owner of this certificate (buyer) to the issuer (seller).', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
