INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Loopring', 'LRC', 'lrc.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://loopring.org/', '@loopringorg', 'Loopring is an open, multilateral token exchange protocol for decentralized exchange on the Ethereum blockchain. Loopring is intended to serve as a common building block with open standards, driving interoperability among decentralized applications (DAPPs) that incorporate exchange functionality. Trades are executed by a system of Ethereum smart contracts that are publicly accessible, free to use, and that any dApp can hook into. Loopring’s token is based on the ERC20 Ethereum Token Standard and can be liquidated through a Loopring smart contract.', '', '', '', '2017-06-08', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Hshare', 'HSR', 'hsr.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW/PoS', 'https://h.cash/', '@Mkt_Hcash', 'Hshare is a dual network blockchain comprised both of a blockchain-based network and a DAG-based network. It allows for anonymous transactions through the use of zero proof technology. The blockchain-based side of Hshare features a PoW/PoS cryptocurrency.', '', '', '', '2017-02-10', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Zenome', 'ZNA', 'zna.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://zenome.io/', '@ZenomePlatform', 'The Zenome project is a decentralized blockchain-driven database of genomic information. The platform supports the possibility to manage your genomic data while maintaining privacy and ability to make a profit from selling access to different parts of the genome. Some nodes are resource providers that store and process genetic information. Data exchange is secured through encryption and utilization of blockchain smart contracts.\n\nThe ZNA token (Zenome Token) is a utility token that powers decentralized economics of the Zenome platform. The token has the following functionalities: Payment - ZNA is the exchange unit in the internal ecosystem; Economic incentive - Engage users in interactions on the platform; Nodes are rewarded for providing disk space and CPU time Participants are rewarded for opening their genetic information. Security Deposit - A security deposit is required for secure data exchange.\n\nZenome will be holding its Presale on October 17, 2017. There will be a total of 320M tokens available, for $0.3 each at the offering.\n\nThe main ICO details are yet to be announced. If you wish to learn more about Zenome main ICO, please visit the[token sale terms](https://zenome.io/download/terms.pdf) page.', '', '', '', '2018-07-09', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'EZMarket', 'EZM', 'ezm.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://ezmarket.io/', '@ezmarketsupport', 'EZMarket is a decentralized application built on the Ethereum blockchain. It has no centralized authority and it does not require a middleman to function or to manage user’s information.\n\nSmart contracts are the bridge on the blockchain to connect buyers and sellers. Transactions are completed in a trustless manner and automatically.', '', '', '', '2019-04-09', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Obsidian', 'ODN', 'odn.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoS', 'https://obsidianplatform.com/', '@ObsidianCrypto', 'Obsidian is a PoS cryptocurrency for popular use. It uses the more energy efficient SHA-512 hash algorithm to optimize for use on mobile devices and improved quantum computing resilience. ODN uses the proof-of-stake consensus strategy and pays a block reward of 20 ODN, which is equivalent to an interest rate of about 10%.\n\nIn addition, Obsidian is the currency that powers to future Obsidian Secure Anonymous Messenger economy. The Obsidian Secure Anonymous Messenger allows for sending end-to-end encrypted messages, media, and files and is optimized for anonymity of communication and metadata protection/avoidance and does not require any user accounts, phone number or personal email address which could identify its users. The message transport will be accomplished by a decentralized network of special messaging masternodes that can be run by anyone by simply installing the respective software. Running messaging masternodes will be rewarded in Obsidian currency, so that messenger users and messenger masternode hosters form an economy which is independent from any company or country and maintained only by the open source community.\n\nObsidian has an active Slack community in English with channels in русский and 中文, which welcomes traders, entrepreneurs, developers and everyone interested in science and innovation to share knowledge and learn.\n\nAccording to the White Paper, the current circulating supply is 24.4M.', '', '', 'SHA-512', '2018-12-08', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ClearPoll', 'POLL', 'poll.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.clearpoll.io/', '@voteclearpoll', 'ClearPoll aims to provide more accurate public opinion data. This is done by allowing wider participation and by making polls easily accessible via the ClearPoll app. ClearPoll allows users to vote on topics, and then the topics with the most votes become active polls for everyone to vote on. Meaning that people are deciding what matters to them the most, and then people are voting on that issue. It is a transparent and accurate way of gauging public opinion.\n\nIt is ClearPoll\'s intention to use blockchain technology to record votes on polls. This means that once a vote has been cast on a poll, it is sent to the blockchain, and cannot be changed or removed. Final poll results are also stored on the blockchain, meaning poll result data is final and 100% publicly viewable by all ClearPoll users. Nobody can hide or manipulate what the general public has to say.\n\nThe number of tokens generated is subject to the funding received during the PreICO and ICO. ClearPoll tokens are an ERC20 token.', '', '', '', '2017-11-10', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Moya Token', 'MTK', 'mtk.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://moyanetworks.com/', '@MoyaNetworks', 'Moya Networks is a project that aims to provide submarine capacity and terrestrial capacity obtained to offer a range of lower priced world class Synchronous Digital Hierarchy (“SDH”) and Internet Protocol (“IP”) network transport services to the a wide range of African Internet Service Providers (ISP’s),international carriers, content providers, large multi-national businesses and governments. Moya Tokens (MTK) are ERC20 tokens based on the Ethereum blockchain that grants the holder the right to receive 20% of MTK\'s profit on an annual basis.', '', '', '', '2017-01-10', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Cashaa', 'CAS', 'cas.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://cashaa.com/', '@CashaaLtd', 'Cashaa is an Ethereum-based banking platform powered by the technology of Auxledger. It offers an exchange and money transfer services between fiat and cryptocurrencies. financial technology combined with fiat will enable real-time, peer-to-peer value exchange and payment services across all the Blockchain. The integrated Cashaa\'s wallet system enables its community to save, spend, borrow and get insured, with a simplified user experience in a legally compliant way.\n\nCAS, an EIP-20 token, is used as the main currency of Cashaa\'s ecosystem and allows its holders to acquire premium services, trade cryptocurrencies anywhere in the world, provide the credit score for lenders, participate in governing mechanism of CAS usage, and publicly trade tokens.', '', '', '', '2017-12-10', '2001-01-01 00:00:00');
