INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bismuth', 'BIS', 'bis.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoW', 'http://bismuth.cz/', '@CryptoBismuth', 'Bismuth is a Proof of Work cryptocurrency based on the SHA224 algorithm. It comes with a set of dApps supplied as interpretation engines, which prevent blockchain bloat.\n\nOn-chain messaging and data storage is available in both public and encrypted forms.\n\nBy reformulating the cryptocurrency code Bismuth aims to simplify its readability, make it compatible across all platforms and integrate it into business solutions. Bismuth is not based on code of BTC or any of it’s derivatives, it is only inspired by some ideas laid down by Andreas M. Antonopoulos, Satoshi Nakamoto (BitCoin), Sunny King (Peercoin), NXT and ETH developers.', '', '', '', '2017-01-05', '2001-01-01 00:00:00');
