INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Javvy', 'JVY', 'jvy.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://javvy.com/', '@https://twitter.com/javvycrypto', 'Javvy is a full-service crypto wallet and exchange. Javvy determines each country\'s regulatory requirements in advance and requests only the documentation necessary for compliance resulting in a rapid ID verification process that ensures no time is wasted on registration.\n\nJVY is an ERC 20 token based on the Ethereum blockchain. It will provide a common framework for buying into and selling out of cryptocurrency. Users will have the option to hold, sell, or convert it to nearly any other cryptocurrency.', '', '', '', '2019-05-09', '2001-01-01 00:00:00');
