INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Leverj', 'LEV', 'lev.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.leverj.io/', '@Leverj_io', 'Leverj is a decentralized leveraged crypto exchange that provides solutions to the inherent risks seen in centralized trading platforms. Leverj provides decentralized identity to avoid identity leaks and multi-signature accounts to provide complete user control over account funds. LEV is an Ethereum-based ERC20 token and it is the primary token in Leverj\'s two-level token system.\n\nThe LEV token represents a license to transact on the platform proportional to the percentage ownership of the token supply. The second token, FEE, can be generated by LEV token holders by freezing the LEV token in a smart contract for a fixed duration, allowing them to trade on the Leverj platform without having to pay fees in ETH.', '', '', '', '2017-07-11', '2001-01-01 00:00:00');
