INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Dynamic Trading Rights', 'DTR', 'dtr.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://www.tokens.net/', '@TokensNet', 'Tokens.net is a cryptocurrency trading platform that allows users to exchange ERC20 tokens and other cryptocurrencies. Trading fees within the platform are paid in Dynamic Trading Rights (DTR), an ERC20 token on the Ethereum blockchain. DTR is charged as trading fees based on the market price of DTR at the moment of the trade. A transaction for a DTR holder will be charged at the best ask price, ignoring the market depth. Users that do not hold DTR will be charged a fee in the currency they are trading in. Conversion of the fee cost into DTR will be executed automatically and clients owning DTR will have their DTR subtracted from their balance, based on the DTR’s market value.', '', '', '', '2019-07-10', '2001-01-01 00:00:00');
