INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Lendoit', 'LOAN*', 'loan_.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://lendoit.com/', '@Lend0it', 'Lendoit is a decentralized P2P Lending platform that provides all elements lending markets need to function. These include: Professional scoring and verification APIs for each country, a loan marketplace, where lenders bid interest rates on loan applications and a default market where failed loans can be traded. The Landoit smart loan contract also offers functions like syndicated loans and the ability to sell a loan to another lender if needed. LOAN is an ERC20 token based on the Ethereum blockchain. It is used as the main currency in the platform.', '', '', '', '2017-02-09', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'ZABERcoin', 'ZAB', 'zab.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'http://www.zabercoin.io/', '@zabercoin', 'ZABERcoin is a crowdfunding service specialized in real estate sales. Within ZABERcoin\'s ecosystem, each token will be backed by a lucrative property such as real estate making it a sustainable source of income. ZAB is an ERC20 token based on the Ethereum blockchain.', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Modulum', 'MDL', 'mdl.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://modulum.io/', '@HitechOne_', 'Hitech One is a french company specialized in the conception and production of miniaturized smart and modular embedded systems. Modulum, created by Hitech, is an IoT device able to interact with the real world using sensors, communication boards and actuators. Modulum’s hardware is modular in order to adapt to the consumer \'s final needs. Modulum is able to record real-world data to blockchains (public or private), or to any other network and web services (web servers, clouds, etc.). To give Modulum its utility, an application must be installed. The user is able to create his own embedded application and make it communicate with the external services and the blockchains that suit his specific needs. Modulum usages range from simply securing that a product is delivered according to a customer and insurance parameters to entire smart cities (energy infrastructures’ management, population flow management, financial independence of infrastructures.).\n\nMDL is an Ethereum-based token that represents a real exchange value within the Modulum ecosystem. The tokens allow Modulum users, Hitech One, add-on creators, application creators, resellers, government and legal parties, etc., to create, redeem and use associated services.', '', '', '', '2019-02-10', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Bitfinex Bitcoin Future', 'BT1', 'bt1.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, 'BT1 is a ChainSplit Token ([CST](https://www.bitfinex.com/posts/221​)) issued by Bitfinex which is a token that resembles the price of BTC after the SegWit2x hardfork that is occuring block 494784 (Mid November, 2017). Traders are using this token to speculate on the success of the B2X hard fork. The SegWit2x version of Bitcoin has been given the BT2 symbol by bitfinex and resembles the future price of B2X after the hardfork.\n\nBitfinex describes the Bitfinex Bitcoin Future as \'Incumbent Bitcoin Blockchain\' as the currently existing Bitcoin blockchain that is exclusively used based on the consensus protocol as set out from time to time in the repository https://github.com/bitcoin/bitcoin;\n\nLearn more about bitcoin forks [here](https://www.cryptocompare.com/coins/guides/a-guide-to-bitcoin-forks/) and about BTC [here](https://www.cryptocompare.com/coins/btc/overview)', '', '', '', '2017-05-10', '2001-01-01 00:00:00');
