INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Ties Network', 'TIE', 'tie.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '@tiesnetwork', 'Ties Network is a decentralized social platform where business professionals can connect and strike business deals via smart-contracts in no time. The platform uses a trustworthy rating system, thus making sure all participants can focus solely on business and benefits of beneficial relationships, and leave the verification and trust issues to the platform’s integrated solutions.\n\nTies Network is based on TiesDB – a public, decentralized, and distributed noSQL database that allows to store huge amounts of dynamic data, and search within the content of the files. Ties DB is a public, open-source solution that can be utilized by other DAPPS and decentralized blockchain-related projects to facilitate their entering the market and structure large amounts of data.', '', '', '', '2017-07-08', '2001-01-01 00:00:00');
