INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Covesting', 'COV', 'cov.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://covesting.io/', '@covesting', 'The Covesting platform was conceived to help investors and cryptocurrency traders getting in touch with each other. Investors can browse through dozens of trading strategies, provided by professional asset managers, and subscribe to the ones matching their goals.\n\nThanks to technology-based solutions and smart-contracts, the Covesting platform allows its users to replicate trading activity of a chosen Model directly into their segregated account at Covesting.', '', '', '', '0001-01-01', '2001-01-01 00:00:00');
INSERT INTO `coins`(`coin_id`, `name`, `symbol`, `logo`, `price_usd`, `price_btc`, `volume_usd_24h`, `market_cap_usd`, `available_supply`, `total_supply`, `max_supply`, `percent_change_1h`, `percent_change_24h`, `percent_change_7d`, `proof_type`, `website`, `twitter`, `description`, `features`, `technology`, `algorithm`, `start_date`, `last_updated`) VALUES (NULL, 'Jibrel Network Token', 'JNT', 'jnt.jpg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'https://jibrel.network/', '@www.twitter.com/jibrelnetwork/', 'Jibrel Network provides currencies, equities, commodities and other financial assets and money  instruments as standard ERC-20 tokens on the Ethereum blockchain. In the medium to long-term,  Jibrel will focus on tokenizing debt instruments and their derivatives- thereby streamlining large  portions of retail investment and investment banking operations.\n\n  \nJirbel are the first protocol to allow anyone to put traditional assets such as currencies, bonds, and  other financial instruments on the blockchain in a form of Crypto Depository Receipts (CryDRs).  Our CryDRs have smart regulation built in; real-world rules and regulations are translated into  solidity code to ensure that while decentralized, tokenized financial assets always follow real world  rules and regulations.', '', '', '', '2020-02-01', '2001-01-01 00:00:00');
